    job_id INT PRIMARY KEY AUTO_INCREMENT,
    job_role VARCHAR(50) NOT NULL,
    job_desc TEXT NOT NULL, 
    job_type VARCHAR(50) NOT NULL, 
    city VARCHAR(100) NOT NULL,
    company VARCHAR(100) NOT NULL,
